import React, { Component } from 'react';
import axios from "axios";
import classes from "./order.module.css";
import { Tag } from 'antd';
import { Table, Divider } from 'antd';
// import classes from'./Header.module.css';
const columns = [
  {
    title: 'Name',
    dataIndex: 'name',
    key: 'name',
    render: text => <a>{text}</a>,
  },
  {
    title: 'ID',
    dataIndex: 'age',
    key: 'age',
  },
  {
    title: 'Address',
    dataIndex: 'address',
    key: 'address',
  },
  {
    title: 'Tags',
    key: 'tags',
    dataIndex: 'tags',
    render: tags => (
      <span>
        {tags.map(tag => {
          let color = tag.length > 5 ? 'geekblue' : 'green';
          if (tag === 'loser') {
            color = 'volcano';
          }
          return (
            <Tag color={color} key={tag}>
              {tag.toUpperCase()}
            </Tag>
          );
        })}
      </span>
    ),
  },
  {
    title: 'Action',
    key: 'action',
    render: (text, record) => (
      <span>
        <a>Invite {record.name}</a>
        <Divider type="vertical" />
        <a>Delete</a>
      </span>
    ),
  },
];

class Preorders extends Component{
    constructor(props) {
        super(props);
        this.state = {
           order:[
                {MCH_ID:0,
                ORDER_BACON :0,
                ORDER_CHEESE :0,
                ORDER_MEAT :0,
                ORDER_REMARK :0,
                ORDER_SALAD :0,
                TOTAL_FEE :0,
                USER_ADDRESS:"" ,
                USER_NAME :"",
                USER_PHONE :"",},
        
           ]
            }
            
        }
      
      componentDidMount(){
        axios.get("https://5y5jmjim9l.execute-api.cn-northwest-1.amazonaws.com.cn/prod/order", { params: { MCH_ID: "41708307" }})
          .then(res => {
            console.log(res);
            this.setState({
                order:res.data.msg,
            })
            
        
          })
          .catch(err => {
            console.log(err);  
          })}
 render(){


