                    TOTAL_FEE :0,
                    USER_ADDRESS:"" ,
                    USER_NAME :"",
                    USER_PHONE :"",}
           ]
            }
            
        }
      
      componentDidMount(){
        axios.get("https://5y5jmjim9l.execute-api.cn-northwest-1.amazonaws.com.cn/prod/order", { params: { MCH_ID: "41735001" }})
          .then(res => {
            console.log(res);
            this.setState({
                order:res.data.msg,
            })
            
        
          })
          .catch(err => {
            console.log(err);  
          })}
 render(){


