import React, { Component, Fragment } from 'react';
import Burger from '../../Components/Burger/Burger';
import BuildControls from '../../Components/Burger/OrderSummary/BuildControls';
import Modal from '../../Components/UI/Model/Msgbox';
import {connect} from 'react-redux';
import * as actions from '../../store/actions/index';
// const ingredient_prices = {
//     salad: 0.5,
//     cheese: 0.4,
//     meat: 1.3,
//     bacon: 0.7
// }
class BurgerBuilder extends Component {
    state = {
        totalPrice: 0,
        purchaseable: false,
        visible:false,
      
    }

changeModal =()=>{
        this.setState({
            visible:false
        })
    }
showModal = () => {
        this.setState({
            visible: true,
        });
}
isBurgerPurchasable(ingredients) {
    const sum = Object.keys(ingredients)
                    .map(igKey => {
                        return ingredients[igKey];
                    })
                    .reduce((sum,el)=>{
                        return sum + el;
                    },0);

    return sum>0;
}

handleConfirm = () => {
    this.setState({
        visible: false
    })
    this.changeModal(); 
    return (
        <Burger/>)
    
}
render(){
    let mainburger=null;
    const disableInfo = {
        ...this.props.ings
   }
   for(let key in disableInfo){
      disableInfo[key] = (disableInfo[key] <= 0) ? true : false;
   }
    if(this.props.ings){
        mainburger= (
           <Fragment>
                <Burger ingredients = {this.props.ings}/>
                <BuildControls
                totalPrice={this.props.price}
                IngredientAdded={this.props.onIngredientAdded}
                RemoveIngredient={this.props.onIngredientRemoved}
                disabled={disableInfo}
                prsable={this.isBurgerPurchasable(this.props.ings)}
                click={this.showModal}/>
           </Fragment>
        );}
        let modal=null;
       if(this.state.visible){
           modal=(<Modal
           visible={this.state.visible}
           title='your order'
           change={this.changeModal}
        //    totalprice1={this.state.totalPrice}
        //    ingredients1={this.state.ingredients}
           handleConfirm={this.handleConfirm}
           >
          <ul>Current Price: £{this.props.price.toFixed(2)} </ul>
           <ul>salad:{this.props.ings.salad}</ul>
           <ul>bacon:{this.props.ings.bacon} </ul>
           <ul>cheese:{this.props.ings.cheese}  </ul>
           <ul>meat:{this.props.ings.meat} </ul>
           </Modal>)
       }
       
        console.log(this.state.visible)
        return (<Fragment>
                {mainburger},
                {modal}
         </Fragment>
        );
    }
}
const mapStateToProps = state => {
    console.log( state.burgerBuilder.ingredients)
    return {
        ings: state.burgerBuilder.ingredients,
        price: state.burgerBuilder.totalPrice,
      
    };
}

const mapDispatchToProps = dispatch =>{
    return {
        onIngredientAdded: (ing) => dispatch(actions.addIngredient(ing)),
        onIngredientRemoved: (ing) => dispatch(actions.removeIngredient(ing)),
      
        
    };
}

export default connect(mapStateToProps, mapDispatchToProps)(BurgerBuilder);
