import React, { Component, Fragment } from 'react';
import Burger from '../../Components/Burger/Burger';
import BuildControls from '../../Components/Burger/OrderSummary/BuildControls';
import Modal from '../../Components/UI/Model/Msgbox';
const ingredient_prices = {
    salad: 0.5,
    cheese: 0.4,
    meat: 1.3,
    bacon: 0.7
}
class BurgerBuilder extends Component {
    state = {
        ingredients: {
            salad: 0,
            bacon: 0,
            cheese: 0,
            meat: 0
        },
        totalPrice: 0,
        purchaseable: false,
        visible:false,
        sumprice:{
            salad: 0,
            bacon: 0,
            cheese: 0,
            meat: 0
        }
    }
    addIngredientHandler = (type) => {
        const updatedIngredients = {
                ...this.state.ingredients
        }//这样的话不会改变原来的ingredients的值，相当于复制该数组。 
        // const newPrice = ingredient_prices[type];//增加的原料的价格
        const updatedsumprice = {
            ...this.state.sumprice
        }             
        updatedIngredients[type] = this.state.ingredients[type] + 1;//更新原料的数量
        const newPrice = this.state.totalPrice + ingredient_prices[type];
        updatedsumprice[type]=ingredient_prices[type]* updatedIngredients[type];//每一个原料的总价
        this.setState({
            ingredients: updatedIngredients,
            totalPrice: newPrice,
            sumprice:updatedsumprice
        });
       
        if(newPrice>0){ //注意这里判断的是newprice,totalprice还没更新
            this.setState({ purchaseable:true})
        }
        console.log( type,this.state.sumprice[type])
    }
    removeIngredientHandler = (type) =>{
        const updatedIngredients = {
            ...this.state.ingredients
        }
        const updatedsumprice = {
            ...this.state.sumprice
        }      
        updatedIngredients[type] = this.state.ingredients[type] - 1;
        const newPrice = this.state.totalPrice - ingredient_prices[type];    
        updatedsumprice[type]=ingredient_prices[type]* updatedIngredients[type];
        this.setState({
            ingredients: updatedIngredients,
            totalPrice: newPrice,
            sumprice:updatedsumprice
        });
         if(this.state.newPrice>0){
             this.setState({ purchaseable:true})
         }//还没想好怎么把同步newprice
    }
    showModal = () => {
        this.setState({
            visible: true,
        });
}

    updateorderstate=()=>{ 
        this.state.orderstate=true;
          }
    render(){
        let showmodal=null;
       if(this.state.visible){
           showmodal=<Modal
           visible={this.state.visible}
           title='your order'
           totalPrice={this.state.totalPrice}
           sumprice={this.state.sumprice}
           ingredients={this.state.ingredients}
           >
          <p>Current Price: <strong>£{this.state.totalPrice.toFixed(2)}</strong> </p>
           <p>salad:{this.state.ingredients.salad}  <strong>£price:{this.state.sumprice.salad.toFixed(2)}</strong></p>
           <p>bacon:{this.state.ingredients.bacon} <strong>£price:{this.state.sumprice.bacon.toFixed(2)}</strong></p>
           <p>cheese:{this.state.ingredients.cheese}<strong>£price:{this.state.sumprice.cheese.toFixed(2)}</strong> </p>
           <p>meat:{this.state.ingredients.meat} <strong>£price:{this.state.sumprice.meat.toFixed(2)}</strong></p>
           </Modal>
       }
        const disableInfo = {
             ...this.state.ingredients
        }
        for(let key in disableInfo){
           disableInfo[key] = (disableInfo[key] <= 0) ? true : false;
        }
        console.log(this.state.visible)
        return (
           <Fragment>
                <Burger ingredients = {this.state.ingredients}/>
                <BuildControls 
                    sumprice={this.state.sumprice}   
                    totalPrice={this.state.totalPrice}
                    IngredientAdded={this.addIngredientHandler}
                    RemoveIngredient={this.removeIngredientHandler}
                    disabled={disableInfo}
                    purchaseable={this.state.purchaseable}
                    click={this.showModal}
                />
                {showmodal}
            </Fragment>
       
        );
    }
}
export default BurgerBuilder;
