import { Input } from 'antd';
import { Select } from 'antd';
import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Link,Switch,withRouter} from 'react-router-dom';
import Burger from '../Components/Burger/Burger';
import BurgerBuilder from '../Container/BurgerBuilder/BurgerBuilder';
class Info extends Component{

  
  state = {
    order:[
      {MCH_ID:0,
      ORDER_BACON :0,
      ORDER_CHEESE :0,
      ORDER_MEAT :0,
      ORDER_REMARK :0,
      ORDER_SALAD :0,
      TOTAL_FEE :0,
      USER_ADDRESS:"" ,
      USER_NAME :"",
      USER_PHONE :"",}],

      ingredients2: {
        salad: 0,
        bacon: 0,
        cheese: 0,
        meat: 0
    },
    }
    componentDidMount()
  {
    console.log(this.props.location.ingredients)
    this.setState({
      ingredients2:this.props.location.ingredients})
    }
  
  handleChange(value) {
    console.log(`selected ${value}`);
  }
  fn(data) {
    this.setState({
        ingredients: data //把父组件中的parentText替换为子组件传递的值
    })
     }
render(){
  const { Option } = Select;
  let form=
  <div>
    <Input placeholder="Your Name" />
    <Input placeholder="Street" />
    <Input placeholder="ZIP CODE" />
    <Input placeholder="Country" />
    <Input placeholder="Email" />
    <Select defaultValue="fastest" style={{ width: 240 }} onChange={this.handleChange}>
      <Option value="jack">Jack</Option>
      <Option value="lucy">Lucy</Option>
    </Select>
  </div>
  
  return(
    <div>
    {form}
    <Burger ingredients = {this.state.ingredients2} />
    </div>)
  }
}
export default withRouter( Info);
