class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
        visible: false,
    }
}

showModal = () => {
    this.setState({
        visible: true,
    });
}
onClose = () => {
console.log('onClose');
}
onConfirm = () => {
console.log('onConfirm');
}
  render() {
    const { visible } = this.state
    return ( 
      <div>
      <BurgerBuilder
      visible1={visible}
      />
      <Modal
            visible={visible}
            title='这是自定义的title'
            onClose={this.onClose}
            onConfirm={this.onConfirm}
            >
            这是自定义的content
            </Modal>   
            <button onClick={() => this.showModal()} style={{
                'background': '#4CA791',
                'color': '#fff',
                'border': 'none',
                'width': 300,
                'height': 50,
                'fontSize': 30,
            }}>切换显隐</button>
      console.log(visible)     
    </div>);
  }
}
export default App;
