import React, { Component } from 'react';
import axios from "axios";
import classes from "./order.module.css";
import { Tag } from 'antd';
import { Table, Divider } from 'antd';
// import classes from'./Header.module.css';
const columns = [
  {
    title: 'Name',
    dataIndex: 'USER_NAME',
    key: 'USER_NAME',
    render: text => <a>{text}</a>,
  },
  {
    title: 'ID',
    dataIndex: 'MCH_ID',
    key: 'MCH_ID',
  },
  {
    title: 'Address',
    dataIndex: 'USER_ADDRESS',
    key: 'USER_ADDRESS',
  },
  {
    title: 'bacon',
    dataIndex: 'ORDER_BACON',
    key: 'ORDER_BACON',
    className:'bacon'
  },
  {
    title: 'salad',
    dataIndex: 'ORDER_SALAD',
    key: 'ORDER_SALAD',
  },
  {
    title: 'cheese',
    dataIndex: 'ORDER_CHEESE',
    key: 'ORDER_CHEESE',
  },
  {
    title: 'meat',
    dataIndex: ' ORDER_MEAT',
    key: ' ORDER_MEAT',
   
  },
  {
    title: 'totalprice',
    dataIndex: 'TOTAL_FEE',
    key: 'TOTAL_FEE',
  },
];

class Preorders extends Component{
    constructor(props) {
        super(props);
        this.state = {
           order:[
                {MCH_ID:0,
                ORDER_BACON :0,
                ORDER_CHEESE :0,
                ORDER_MEAT :0,
                ORDER_REMARK :0,
                ORDER_SALAD :0,
                TOTAL_FEE :0,
                USER_ADDRESS:"" ,
                USER_NAME :"",
                USER_PHONE :"",},
        
           ]
            }
            
        }
      componentDidMount(){
        axios.get("https://5y5jmjim9l.execute-api.cn-northwest-1.amazonaws.com.cn/prod/order", { params: { MCH_ID: "41708307" }})
          .then(res => {
            console.log(res);
            this.setState({
                order:res.data.msg,
            })
            
        
          })
          .catch(err => {
            console.log(err);  
          })}
 render(){


  return( <div className={classes.content} ><br/>
   <h2 className={classes.hope1}> ALL your orders</h2><br/>
<div className={classes.content} > 
    {/* {
        this.state.order.slice(0).reverse().map((one,index) =>{ //相反顺序渲染
            return (
                    <div key={index} >
                      <div className={classes.one}  >
                      <p>Name:<strong>{one.USER_NAME}</strong>   Address:<strong>{one.USER_ADDRESS} </strong></p>
                      
                      <p><strong>ingredients：
                      <Tag>Bacon({one.ORDER_BACON})</Tag>
                      <Tag>Cheese({one.ORDER_CHEESE})</Tag>
                      <Tag>Meat({one.ORDER_MEAT})</Tag>
                      <Tag>Salad({one.ORDER_SALAD})</Tag></strong>
                      </p>
                      <p>price:<strong> £ {Math.round(one.TOTAL_FEE*1000)/1000}</strong> </p>
                    </div>  
                    </div>
               
            )})
    } */}<Table columns={columns} dataSource={this.state.order} />
</div> 
     
     </div>
 )}
}
export default Preorders;
