import React, { Component } from 'react';
import Burger from '../../Components/Burger/Burger';
import BuildControls from '../../Components/Burger/OrderSummary/BuildControls';
const ingredient_prices = {
    salad: 0.5,
    cheese: 0.4,
    meat: 1.3,
    bacon: 0.7
}
class BurgerBuilder extends Component {
    state = {
        ingredients: {
            salad: 0,
            bacon: 0,
            cheese: 0,
            meat: 0
        },
        totalPrice: 0,
        purchaseable: false
    }
    // updatePurchaseState = (ingredients) => {
    //     // const Pricesum = Object.keys(ingredients) //获取到每一个原材料，key是他们的名字
    //     //                 .map(key => ingredients[key])
    //     //                 .reduce((sum, current) => sum + current , 0) //reduce类似一个累加器，可以把所有key的值加起来。
    //     this.setState({purchaseable: Pricesum > 0})
    // }
    // 将每一个原料的数量加起来，如果大于0才可以结算。
//计算每一个原材料的总价
    addIngredientHandler = (type) => {
        const updatedIngredients = {
                ...this.state.ingredients
        }//这样的话不会改变原来的ingredients的值，相当于复制该数组。 
        const newPrice = ingredient_prices[type];//增加的原料的价格                 
        updatedIngredients[type] = this.state.ingredients[type] + 1;//更新原料的数量
        this.setState({
            ingredients: updatedIngredients,
            totalPrice: newPrice
            
        });
        if(this.state.totalPrice>0){
            this.setState({ purchaseable:true})
        }
        console.log(this.state.totalPrice)
        console.log(this.state.purchaseable)
       // this.updatePurchaseState(this.totalPrice);
    }
    removeIngredientHandler = (type) =>{
        const newCount = this.state.ingredients[type] - 1;
        const updatedIngredients = {
            ...this.state.ingredients
        }
        const priceSubstracted = ingredient_prices[type];
        const newPrice = this.state.totalPrice - priceSubstracted;     
        updatedIngredients[type] = newCount;
        this.setState({
            ingredients: updatedIngredients,
            totalPrice: newPrice
        });
        if(this.state.totalPrice>0){
            this.setState({ purchaseable:true})
        }
       // this.updatePurchaseState(updatedIngredients);
    }
    render(){
        const disableInfo = {
             ...this.state.ingredients
        }
        for(let key in disableInfo){
           disableInfo[key] = (disableInfo[key] <= 0) ? true : false;
        }
        return (
           <div>
                <Burger ingredients = {this.state.ingredients}/>
                <BuildControls     
                    totalPrice={this.state.totalPrice}
                    IngredientAdded={this.addIngredientHandler}
                    RemoveIngredient={this.removeIngredientHandler}
                    disabled={disableInfo}
                    purchaseable={this.state.purchaseable}
                />
            </div>
        );
    }
}
export default BurgerBuilder;
