import { Input,DatePicker } from 'antd';
import { Select } from 'antd';
import {
  Form,

  Tooltip,
  Icon,
  Cascader,
 
  Row,
  Col,
  Checkbox,
  Button,
  AutoComplete,
} from 'antd';
import React, { Component } from 'react';
import { BrowserRouter as Router,withRouter} from 'react-router-dom';
import Burger from '../Components/Burger/Burger';
import classes from './form.module.css';
import axios from "axios"; 
 const { TextArea } = Input;
 const { Option } = Select;
  const AutoCompleteOption = AutoComplete.Option;

class Info extends Component{
  
  
  state = {
      totalprice3:0,
      USER_ADDRESS:"" ,
      ORDER_REMARK:"",
      USER_NAME: "",
      USER_PHONE: "",
      ingredients3: {
        salad: 0,
        bacon: 0,
        cheese: 0,
        meat: 0
    },
    confirmDirty: false,
    autoCompleteResult: [],
    }

    handleSubmit = e => {
      e.preventDefault();
      this.props.form.validateFieldsAndScroll((err, values) => {
        if (!err) {
          console.log('Received values of form: ', values);
        }
      });
    };
  
    handleConfirmBlur = e => {
      const { value } = e.target;
      this.setState({ confirmDirty: this.state.confirmDirty || !!value });
    };
  
    compareToFirstPassword = (rule, value, callback) => {
      const { form } = this.props;
      if (value && value !== form.getFieldValue('password')) {
        callback('Two passwords that you enter is inconsistent!');
      } else {
        callback();
      }
    };
  
    validateToNextPassword = (rule, value, callback) => {
      const { form } = this.props;
      if (value && this.state.confirmDirty) {
        form.validateFields(['confirm'], { force: true });
      }
      callback();
    };
  
    handleWebsiteChange = value => {
      let autoCompleteResult;
      if (!value) {
        autoCompleteResult = [];
      } else {
        autoCompleteResult = ['.com', '.org', '.net'].map(domain => `${value}${domain}`);
      }
      this.setState({ autoCompleteResult });
    };
  



    componentDidMount()
  { 
    
     this.setState({
    ingredients3:this.props.location.state.ingredients2,
    totalprice3:this.props.location.state.totalprice2
  })
  console.log(this.state.totalprice3)
    
    }

  handleChange(value) {
    console.log(`selected ${value}`);
  }
  submit=()=>{
    console.log(this.state)
    let order = {
      "MCH_ID": "41708307",
      "ORDER_BACON": this.state.ingredients3.bacon,
      "ORDER_CHEESE": this.state.ingredients3.cheese,
      "ORDER_MEAT": this.state.ingredients3.meat,
      "ORDER_REMARK":this.state.ORDER_REMARK,
      "ORDER_SALAD": this.state.ingredients3.salad,
      "TOTAL_FEE": this.state.totalprice3,
      "USER_ADDRESS": this.state.USER_ADDRESS,
      "USER_NAME": this.state.USER_NAME,
      "USER_PHONE": this.state.USER_PHONE,
    }
    console.log(order)
    axios.post("https://5y5jmjim9l.execute-api.cn-northwest-1.amazonaws.com.cn/prod/order", order)
  .then(res => {
    console.log(res);
  })
  .catch(err => {
    console.log(err);  
  })
     
    this.props.history.push("/order");
    this.props.history.go();
 
  }
  handlename = (event)=>{
    if(event && event.target && event.target.value){
      let value = event.target.value;
      this.setState(()=>({USER_NAME:value }))
    }
  }
  handleaddress = (event)=>{
    if(event && event.target && event.target.value){
      let value = event.target.value;
      this.setState(()=>({USER_ADDRESS:value }))
    }
  }
  handlephone = (event)=>{
    if(event && event.target && event.target.value){
      let value = event.target.value;
      this.setState(()=>({USER_PHONE:value }))
    }
  }
  handleremark = (event)=>{
    if(event && event.target && event.target.value){
      let value = event.target.value;
      this.setState(()=>({ORDER_REMARK:value }))
    }
  }
  checkEmail(rule, value, callback) {
    var re = /^(\w-*\.*)+@(\w-?)+(\.\w{2,})+$/;
    if(re.test(value)){
        callback('The input is not valid E-mail!');
    }else{
      callback('Please input your E-mail!')
    }
};
 isNull(str) {
  if (str == "") return true;
  else return false
}

checkname(rule, value, callback) {
  var re = /^[a-zA-Z\u4e00-\u9fa5]+$/;
  if(!re.test(value)&&(!this.isNull(value))){
      callback("Please input Chinese character or letter")
  }
  if(this.isNull(value)){
      callback('Please input your name!')
  }
};
  checkPhone(rule, value, callback) {
    if(!(/^1(3|4|5|7|8)\d{9}$/.test(value))){
        callback("The mobile phone number is wrong");
    }else{
        callback("Please input your phone number")
    }
};
render(){
  const { getFieldDecorator } = this.props.form;
  const formItemLayout = {
    labelCol: {
      xs: { span: 24 },
      sm: { span: 8 },
    },
    wrapperCol: {
      xs: { span: 24 },
      sm: { span: 16 },
    },
  };

    let buttons=
    <div className={classes.butn}>
      <button className={classes.confirm}
      onClick={()=>{
        document.getElementById("info").scrollIntoView();
      }}>确定
      </button>
      <button className={classes.close}
      onClick={()=>{
        this.props.history.push( "/");
        this.props.history.go();
    }}
      >取消</button>
    </div>

  const { Option } = Select;
  let forms=
    <div className={classes.form}>
      <h2><strong>请输入你的联系信息</strong></h2>
      <Form {...formItemLayout} >
          <Form.Item label="name">
                    {getFieldDecorator('name', {
                    rules: [
                    {
                      validator: this.checkname
                  }
                  ],
                })(<Input  style={{ width: 300 }}
                onChange ={event => this.handlename(event)}/>)}
          </Form.Item>
          <Form.Item label="Street">
              <Input placeholder="Your current address." style={{ width: 300 }}
              onChange ={event => this.handleaddress(event)} />
          </Form.Item>

          <Form.Item label="Phone Number">
                {getFieldDecorator('phone', {
                  rules: [ { 
                    validator: this.checkPhone, }],
                  },
                )(<Input  style={{ width: 300 }}
                  onChange ={event => this.handlephone(event)}
                />)}
          </Form.Item>
          <Form.Item label="E-mail">
                {getFieldDecorator('email', {
                  rules: [
                    {
                      validator: this.checkEmail,
                    }
                  ],
                })(<Input  style={{ width: 300 }}/>)}
          </Form.Item>
          
          <Form.Item label="remark">
          <TextArea  rows={3} placeholder="For example, don't pepper." style={{ width: 300 }} 
          onChange ={event => this.handleremark(event)}/><br/>
          </Form.Item>

          <Select defaultValue="fastest" style={{ width: 400 }} onChange={this.handleChange}>
          <Option value="lowest">Jack</Option>
          <Option value="middlest">Lucy</Option>
          </Select><br/><p></p>
          <DatePicker style={{ width: 400 }}  /><br/>
        </Form>    
      <button className={classes.order} onClick={this.submit} 
      >下单</button>
    </div>

  return(
   <div>
     <h1 className={classes.hope} ><strong> We hope is tastes well !!:)</strong></h1>
    <Burger ingredients = {this.state.ingredients3} /><br/>
  <h2  className={classes.hope1}> totalprice:<strong> £{Math.round(this.props.location.state.totalprice2*100)/100}</strong></h2>
    {buttons}
    <div id="info"></div>
     {forms}
     <br/><p></p>
     
    </div>)
  }
}
export default withRouter(Form.create()( Info));
