import React, { Component, Fragment } from 'react';
import Burger from '../../Components/Burger/Burger';
import BuildControls from '../../Components/Burger/OrderSummary/BuildControls';
import Modal from '../../Components/UI/Model/Msgbox';
import {connect} from 'react-redux';
import * as actions from '../../store/actions/index';
// const ingredient_prices = {
//     salad: 0.5,
//     cheese: 0.4,
//     meat: 1.3,
//     bacon: 0.7
// }
class BurgerBuilder extends Component {
    state = {
        // ingredients: {
        //     salad: 0,
        //     bacon: 0,
        //     cheese: 0,
        //     meat: 0
        // },
        totalPrice: 0,
        purchaseable: false,
        visible:false,
        // sumprice:{
        //     salad: 0,
        //     bacon: 0,
        //     cheese: 0,
        //     meat: 0
        // }
    }
// addIngredientHandler = (type) => {
//         const updatedsumprice = {
//             ...this.state.sumprice
//         }             
//         updatedsumprice[type]=ingredient_prices[type]* updatedIngredients[type];//每一个原料的总价
//         this.setState({
//             sumprice:updatedsumprice
//         });
//         this.props.addIngredient;
//     }
// removeIngredientHandler = (type) =>{
//         const updatedsumprice = {
//             ...this.state.sumprice
//         }      
//         updatedsumprice[type]=ingredient_prices[type]* updatedIngredients[type];
//         this.setState({
//             sumprice:updatedsumprice
//         });
//          this.props.removeIngredient  
//     }
changeModal =()=>{
        this.setState({
            visible:false
        })
    }
showModal = () => {
        this.setState({
            visible: true,
        });
}
handleConfirm = () => {
    this.setState({
        visible: false
    })
    this.changeModal(); 
    return (
        <Burger/>)
    
}
render(){
    let mainburger=null;
    const disableInfo = {
        ...this.state.ingredients
   }
   for(let key in disableInfo){
      disableInfo[key] = (disableInfo[key] <= 0) ? true : false;
   }
    if(this.props.ings){
        mainburger= (
           <Fragment>
                <Burger ingredients = {this.props.ings}/>
                <BuildControls
                totalPrice={this.props.price}
                IngredientAdded={this.props.addIngredient}
                RemoveIngredient={this.props.removeIngredient}
                disabled={disableInfo}
                purchaseable={this.state.purchaseable}
                click={this.showModal}/>
           </Fragment>
        );}
        let modal=null;
       if(this.state.visible){
           modal=(<Modal
           visible={this.state.visible}
           title='your order'
           change={this.changeModal}
        //    totalprice1={this.state.totalPrice}
        //    ingredients1={this.state.ingredients}
           handleConfirm={this.handleConfirm}
           >
          <ul>Current Price: £{this.state.totalPrice.toFixed(2)} </ul>
           <ul>salad:{this.state.ingredients.salad}</ul>
           <ul>bacon:{this.state.ingredients.bacon} </ul>
           <ul>cheese:{this.state.ingredients.cheese}  </ul>
           <ul>meat:{this.state.ingredients.meat} </ul>
           </Modal>)
       }
       
        console.log(this.state.visible)
        return (
                {mainburger},
                {modal}
         
        );
    }
}
const mapStateToProps = state => {
    console.log( state.burgerBuilder.ingredients)
    return {
        ings: state.burgerBuilder.ingredients,
        price: state.burgerBuilder.totalPrice,
    };
}

const mapDispatchToProps = dispatch =>{
    return {
        onIngredientAdded: (ing) => dispatch(actions.addIngredient(ing)),
        onIngredientRemoved: (ing) => dispatch(actions.removeIngredient(ing)),
      
        
    };
}

export default connect(mapStateToProps, mapDispatchToProps)(BurgerBuilder);
