import React, { Component } from 'react';
import axios from "axios";
import classes from "./order.module.css";
import { Tag } from 'antd';
// import classes from'./Header.module.css';
class Preorders extends Component{
    constructor(props) {
        super(props);
        this.state = {
           order:[
                {MCH_ID:0,
                ORDER_BACON :0,
                ORDER_CHEESE :0,
                ORDER_MEAT :0,
                ORDER_REMARK :0,
                ORDER_SALAD :0,
                TOTAL_FEE :0,
                USER_ADDRESS:"" ,
                USER_NAME :"",
                USER_PHONE :"",},
        
           ]
            }
            
        }
      
      componentDidMount(){
        axios.get("https://5y5jmjim9l.execute-api.cn-northwest-1.amazonaws.com.cn/prod/order", { params: { MCH_ID: "41708307" }})
          .then(res => {
            console.log(res);
            this.setState({
                order:res.data.msg,
            })
            
        
          })
          .catch(err => {
            console.log(err);  
          })}
 render(){


  return( <div className={classes.content} >
   <h2> ALL your orders</h2>
<div className={classes.content} >
    {
        this.state.order.map((one,index) =>{
            return (
                    <div key={index} >
                      <div className={classes.one}  >
                      <p>ingredients：
                      <Tag>Bacon({one.ORDER_BACON})</Tag>
                      <Tag>Cheese({one.ORDER_CHEESE})</Tag>
                      <Tag>Meat({one.ORDER_MEAT})</Tag>
                      <Tag>Salad({one.ORDER_SALAD})</Tag>
                      </p>
                      <p>price: <strong>£</strong> {Math.floor(one.TOTAL_FEE*100)/100} </p>
                    </div>  
                    </div>
               
            )})
    }
</div>
      <div className={classes.content}>
      </div>
     </div>
 )}
}
export default Preorders;
