import React from 'react'
import classes from './BuildControls.module.css'
import BuildControl from '../BuildControls/BuildControl';
import { Modal, Button } from 'antd';
const controls = [
    {label : 'Salad', type: 'salad'},
    {label : 'Bacon', type: 'bacon'},
    {label : 'Cheese', type: 'cheese'},
    {label : 'Meat', type: 'meat'},
]
const buildControls = (props) => {
    const ordersum=()=>{ 
        var info='total price:£ '+props.totalPrice;   //从burgerbuilder那里传过来的参数
        console.log(props.totalPrice);
        alert(info);
      }
     let state = { visible: false };

     let showModal = () => {
        this.setState({
          visible: true,
        });
      };

     let handleOk = e => {
        console.log(e);
        this.setState({
          visible: false,
        });
      };

     let handleCancel = e => {
        console.log(e);
        this.setState({
          visible: false,
        });
      };

   return ( <div className={classes.BuildControls}>

        <p>Current Price: <strong>£{props.totalPrice.toFixed(2)}</strong> </p>
        {controls.map(control => {
            return <BuildControl
             key={control.label} 
             label={control.label} 
             add={props.IngredientAdded.bind(this, control.type)}
             remove={props.RemoveIngredient.bind(this,control.type)}
             disabled={props.disabled[control.type]}
             />
        })}
        <button  onClick={this.showModal} //ordersum 点击计算总价按钮之后的账单
        className={classes.OrderButton}
         disabled={!props.purchaseable}>ORDER NOW</button>
         <Modal
          title="Basic Modal"
          visible={this.state.visible}
          onOk={this.handleOk}
          onCancel={this.handleCancel}
        >
          <p>Some contents...</p>
          <p>Some contents...</p>
          <p>Some contents...</p>
        </Modal>
    </div>
   )};

export default buildControls;
