import React, { Component } from 'react'
import classes from './BuildControls.module.css'
import BuildControl from '../BuildControls/BuildControl';
import Modal from '../../UI/Model/Msgbox';
const controls = [//数组
    {label : 'Salad', type: 'salad'},
    {label : 'Bacon', type: 'bacon'},
    {label : 'Cheese', type: 'cheese'},
    {label : 'Meat', type: 'meat'},
]
 this.state = {
    visible: false,
  }
showModal = () => {
        this.setState({
            visible: true,
        });
}
onClose = () => {
    console.log('onClose');
}
onConfirm = () => {
    console.log('onConfirm');
}
class buildControls extends Component{
 render(){

      //Array.map() 方法返回一个新数组，
      //数组中的元素为原始数组元素调用函数处理后的值，同时不会改变原来的数组。

    const ingredientcontrol=(control) => {
        return <BuildControl
         key={control.label} 
         label={control.label} 
         //bind()方法主要就是将函数绑定到某个对象，bind()会创建一个函数，
         //函数体内的this对象的值会被绑定到传入bind()第一个参数的值，
         //例如，f.bind(obj)，实际上可以理解为obj.f()，这时，f函数体内的this自然指向的是obj
         add={props.IngredientAdded.bind(this, control.type)}//IngredientAdded是buildControls定义的
         remove={props.RemoveIngredient.bind(this,control.type)}
         disabled={props.disabled[control.type]}
         />
    }
   return ( 

   <div className={classes.BuildControls}>
     <Modal
    visible={visible}
    title='这是自定义的title'
    onClose={this.onClose}
    onConfirm={this.onConfirm}
    >
      这是自定义的content
            </Modal>
        <p>Current Price: <strong>£{props.totalPrice.toFixed(2)}</strong> </p>
        {/* toFixed() 方法可把 Number 四舍五入为指定小数位数的数字。  */}
        {controls.map(ingredientcontrol)} 
        <button  onClick={() => this.showModal()} //ordersum 点击计算总价按钮之后的账单
         className={classes.OrderButton}
         disabled={!props.purchaseable}>ORDER NOW</button>
    </div>
   )};
  }
export default buildControls;
