import React, { Component } from 'react';
import axios from "axios";
import classes from "./order.module.css";
// import classes from'./Header.module.css';
class Preorders extends Component{
    constructor(props) {
        super(props);
        this.state = {
           order:{
                MCH_ID:0,
                ORDER_BACON :0,
                ORDER_CHEESE :0,
                ORDER_MEAT :0,
                ORDER_REMARK :0,
                ORDER_SALAD :0,
                TOTAL_FEE :0,
                USER_ADDRESS:"" ,
                USER_NAME :"",
                USER_PHONE :"",
            }
            
        }
      }
 render(){
axios.get("https://5y5jmjim9l.execute-api.cn-northwest-1.amazonaws.com.cn/prod/order", { params: { MCH_ID: "41735001" }})
  .then(res => {
    console.log(res);
    this.setState({
        MCH_ID : res.data.msg.MCH_ID,
        ORDER_BACON :res.ORDER_BACON,
        ORDER_CHEESE :res.ORDER_CHEESE,
        ORDER_MEAT :res.ORDER_MEAT,
        ORDER_REMARK :res.ORDER_REMARK,
        ORDER_SALAD :res.ORDER_SALAD,
        TOTAL_FEE :res.TOTAL_FEE,
        USER_ADDRESS:res.USER_ADDRESS ,
        USER_NAME :res.USER_NAME,
        USER_PHONE :res.USER_PHONE,
          
    })
    

  })
  .catch(err => {
    console.log(err);  
  })

  return( <div >
      <p> 
        your order
      </p>
      <div className={classes.content}>
          res.date.msg
      <li>name:{this.state.order.USER_NAME}</li>
      <li>xuehao:{this.state.order.MCH_ID}</li>
      <li>total fee:{this.state.order.TOTAL_FEE}</li>
      <li>address:{this.state.order.USER_ADDRESS} </li>
      <li>phone number:{this.state.order.USER_PHONE} </li>
      <li>salad:{this.state.order.ORDER_SALAD} </li>
      <li>bacon:{this.state.order.ORDER_BACON} </li>
      <li>cheese:{this.state.order.ORDER_CHEESE} </li>
      <li>meat:{this.state.order.ORDER_MEAT} </li>
      </div>
     </div>
 )}
}
export default Preorders;
