import { Input } from 'antd';
import { Select } from 'antd';
import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Link,Switch,withRouter} from 'react-router-dom';
import Burger from '../Components/Burger/Burger';
import BurgerBuilder from '../Container/BurgerBuilder/BurgerBuilder';
import classes from './form.module.css';
import axios from "axios";
class Info extends Component{

  
  state = {
      totalprice:0,
      ingredients3: {
        salad: 0,
        bacon: 0,
        cheese: 0,
        meat: 0
    },
    }

    componentDidMount()
  {  this.setState({
    ingredients3:this.props.location.state.ingredients2,
    totalprice:this.props.location.state.totalprice2
  })
    
    }
  
  handleChange(value) {
    console.log(`selected ${value}`);
  }
  submit(){
    let order = {
      "MCH_ID": "41708307",
      "ORDER_BACON": this.state.ingredients3.bacon,
      "ORDER_CHEESE": this.state.ingredients3.cheese,
      "ORDER_MEAT": this.state.ingredients3.meat,
      "ORDER_REMARK": "我没有提示",
      "ORDER_SALAD": this.state.ingredients3.salad,
      "TOTAL_FEE": this.state.totalprice,
      "USER_ADDRESS": "",
      "USER_NAME": "",
      "USER_PHONE": ""
    }
    axios.post("URL： https://5y5jmjim9l.execute-api.cn-northwest-1.amazonaws.com.cn/prod/order", order)
  .then(res => {
    console.log(res);
  })
  .catch(err => {
    console.log(err);  
  })
  }
 
render(){


  let buttons=
  <div className={classes.butn}>
    <button className={classes.confirm}>确定</button>
    <button className={classes.close}>取消</button>
  </div>

  const { Option } = Select;
  let form=
  <div className={classes.form}>
    <h2>请输入你的联系信息</h2>
    <Input placeholder="Your Name" style={{ width: 300 }} /><br/>
    <Input placeholder="Street" style={{ width: 300 }} /><br/>
    <Input placeholder="ZIP CODE" style={{ width: 300 }}/><br/>
    <Input placeholder="Country" style={{ width: 300 }} /><br/>
    <Input placeholder="Email"  style={{ width: 300 }}/><br/>
    <Select defaultValue="fastest" style={{ width: 300 }} onChange={this.handleChange}>
      <Option value="jack">Jack</Option>
      <Option value="lucy">Lucy</Option>
    </Select><br/>
    <button className={classes.order} onClick={this.submit}>下单</button>
  </div>
  
  return(
    <div >
    <Burger ingredients = {this.state.ingredients3} />
    {buttons}
     {form}
     <br/><p></p>
    </div>)
  }
}
export default withRouter( Info);
